mapper.select:
select member_id memberId, member_name memberName, email
from member
where member_id=#{member_id} and member_password=#{member_password}

select member_salt
from member
where member_id=#{memberId}

select count(member_id)
from member
where member_id = #{memberId}

select member_id memberId, member_name memberName, email
from member
where member_id = #{memberId}

mapper.update:
update member
set email = #{email}
where member_id = #{member_id}
