mapper.select:
select no, title, writer, date_format(write_date, '%Y-%m-%d %H:%i:%s') writeDate, read_count readCount, content
from review_board
where no = #{no}

update review_board
set title = #{title}, content = #{content}
where no = #{no}

mapper.update:
update review_board
set read_count = read_count +1
where no = #{no}
